def main():
    soup = BeautifulSoup(HTML, 'html.parser')
    county_names = [_.text.encode('utf-8') for _ in soup.find_all('li')]
    cleaned_county_names = [_.rsplit(' ', 2)[0].upper() for _ in county_names]
    return cleaned_county_names

if __name__ == '__main__':
    c = main()
